now := GTON.ET;
IF ((now - lastTime) > Sample_time) THEN
	delta_t := TIME_TO_REAL(Sample_time)/millis;
	A := EXP(-delta_t/Pp);
	B := 1.0 - A;
	C := 2.0;

	//PV := A * lastPV + B * Kp * CO + C;
	PV := 0.987 * lastPV - 0.00679 * CO + C;
	lastPV := PV;

	lastTime := now;
END_IF;

now := GTON.ET;
IF ((now - lastTime) > Sample_time) THEN
	delta_t := TIME_TO_REAL(Sample_time)/millis;

	//First calculate net heat
	//Heat_In is our heat source
	//Second term is heat flow depending on temperature difference
	//Obs: At start, set lastTime to start temp Temp_In
	IF lastTime = T#0S THEN
		lastTemp := Temp_In;
	END_IF;
	heat_flow := Heat_In - U * (lastTemp - Temp_Out);

	//Second: calculate temperature change due to heat_flow
	Temp := lastTemp + heat_flow * delta_t / TC;

	//Update variables for next iteration
	lastTemp := Temp;
	lastTime := now;
END_IF;

now := GTON.ET;
IF ((now - lastTime) > SampleTime) THEN
	Level := Level +  TIME_TO_REAL(SampleTime)/millis * (Process_In - Process_Out);
	IF Level < Lower_limit THEN
		Level := Lower_limit;
	ELSIF Level > Upper_limit THEN
		Level := Upper_limit;
	END_IF;
	lastTime := now;
END_IF;

//Check parameters
//Ti division by zero
IF Ti = 0.0 THEN
	Ti := 1.0;
END_IF;

//Update PID parameters every sampleTime
now := GTON.ET;
IF ((now - lastTime) > SampleTime) THEN
	err := SP - PV;
	delta_t := TIME_TO_REAL(SampleTime)/millis;
	CO_P := Kc * err;
	CO_I := CO_I + 1.0/Ti * CO_P * delta_t;
	//Add wind-up for CO_I
	IF CO_I < LowerLimit THEN
		CO_I := LowerLimit;
	ELSIF CO_I > UpperLimit THEN
		CO_I := UpperLimit;
	END_IF;

	//Observe that d(err)/dt = d(SP)/dt - d(PV)/dt
	//If SP constant,
	//d(err)/dt = - d(PV)/dt
	//Good: no output spikes
	//Bad: some processes need more aggressive tuning
	IF delta_t <> 0.0 THEN
		CO_D := Td * (PV - lastPV)/delta_t;
	END_IF;

	//A different approch would be with a tunable filter

	CO := CO_P + CO_I + CO_D;
	//Add wind-up for CO
	IF CO < LowerLimit THEN
		CO := LowerLimit;
	ELSIF CO > UpperLimit THEN
		CO := UpperLimit;
	END_IF;

	lastTime := now;
	lastPV := PV;
END_IF;
